document.head.insertAdjacentHTML('beforeend', `<template id="pop-up-header"><style>:host{display:block;--color:white;user-select:none}#header{color:var(--color);padding:5px 0;margin:0;width:100%;border-collapse:collapse}#header .brand{width:90px;color:silver;font-size:17px;padding-left:10px;white-space:nowrap}#header .brand:hover{color:white}#header .title{text-align:center;font-variant:small-caps;font-size:25px}#header .close{width:100px;text-align:right;font-size:30px;color:silver;padding-right:10px}#header .close span:hover{cursor:pointer;color:white}</style><table id="header"><tr><td class="brand"><slot></slot></td><td class="title"></td><td class="close"><span on-tap="close">&#xD7;</span></td></tr></table></template>`);window.customElements.define('pop-up-header',class extends HTMLElement{constructor(){super(),this.attachShadow({mode:'open'}).appendChild(document.querySelector('template#pop-up-header').content.cloneNode(true));;}$(a){return this.shadowRoot.querySelector(a)}set title(a){void 0,this.$('.title').innerHTML=a}});